from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Message

class RequirementSanityChecker(Component):
    display_name = "Requirement Sanity Checker"
    name = "RequirementSanityChecker"
    description = "Checks requirements for vague language, passive voice, and structural issues."

    inputs = [
        MessageTextInput(
            name="requirements",
            display_name="Requirements"
        )
    ]

    outputs = [
        Output(name="output", display_name="Sanity Check Result", method="build_output")
    ]

    def build_output(self) -> Message:
        reqs = self.requirements.lower()
        issues = []

        # Check for vague words
        vague_terms = ["appropriate", "simple", "easy", "efficient", "minimal", "nice"]
        for term in vague_terms:
            if term in reqs:
                issues.append(f"⚠️ Vague term found: '{term}'")

        # Check for passive voice (basic heuristic)
        if "shall be" in reqs or "must be" in reqs:
            issues.append("⚠️ Passive voice detected — consider using active voice.")

        # Check for short or incomplete requirement sets
        if len(reqs.splitlines()) < 3:
            issues.append("⚠️ Too few requirements — may be incomplete.")

        if not issues:
            return Message(text="✅ No structural issues detected. Requirements look solid.")
        else:
            return Message(text="❌ Rule-based issues:\n" + "\n".join(issues))

